mapper.select:
SELECT A.SQ_NB ,A.DIV_CD ,A.DEAL_DD ,A.RMK_DC ,A.CASH_AM ,A.FR_DT ,A.TO_DT ,A.DEAL_PD ,A.DISP_SQ ,A.CASH_CD ,B.CASH_NM ,A.TR_CD ,C.TR_NM ,A.FTR_CD ,D.TR_NM as FTR_NM ,E.BA_NB_TR ,A.EMP_CD ,S.BANK_NAME FROM AcashFix A LEFT JOIN Acashcd B ON A.CASH_CD = B.CASH_CD LEFT JOIN Strade C ON A.TR_CD = C.TR_CD LEFT JOIN Strade D ON A.FTR_CD = D.TR_CD LEFT JOIN Ftrade E ON A.FTR_CD = E.TR_CD LEFT JOIN FINANCECODE S ON E.BANK_CD = S.FINANCE_CD
A.CO_CD=#{CO_CD} AND A.DIV_CD=#{DIV_CD} AND A.DISP_SQ=#{DISP_SQ}
AND A.CASH_CD = #{CASH_CD}
AND A.TR_CD = #{TR_CD}
AND A.FTR_CD = #{FTR_CD}
AND A.FR_DT BETWEEN #{FR_DT1} AND #{FR_DT2}
AND A.TO_DT BETWEEN #{TO_DT1} AND #{TO_DT2}
ORDER BY A.SQ_NB

mapper.update:
UPDATE AcashFix



        DEAL_DD = #{DEAL_DD}



        ,CASH_CD = #{CASH_CD}



        ,RMK_DC = #{RMK_DC}



        ,CASH_AM = #{CASH_AM}



        ,FR_DT = #{FR_DT}



        ,TO_DT = #{TO_DT}



        ,PJT_CD = #{PJT_CD}



        ,DEAL_PD = #{DEAL_PD}



        ,DEAL_MM = #{DEAL_MM}



        ,DISP_SQ = #{DISP_SQ}



        ,TR_CD = #{TR_CD}



        ,EMP_CD = #{EMP_CD}



        ,FTR_CD = #{FTR_CD}


WHERE
    CO_CD = #{CO_CD}
  AND DIV_CD = #{DIV_CD}
  AND SQ_NB = #{SQ_NB}

mapper.delete:
DELETE FROM AcashFix
WHERE
    CO_CD = #{CO_CD}
  AND DIV_CD = #{DIV_CD}
  AND SQ_NB IN

    #{item}
