mapper.sql:
s.TR_CD IN (
    SELECT
        TR_CD
    FROM StradeRollManage
    WHERE
        CO_CD=#{CO_CD, jdbcType=VARCHAR}
     AND EMP_CD=#{EMP_CD, jdbcType=VARCHAR}
    )
OR s.VIEW_YN = '0'

mapper.select:
SELECT s.CO_CD ,s.TR_CD ,s.TR_NM ,s.USE_YN ,g.REG_NB ,g.PPL_NB ,g.CEO_NM ,g.BUSINESS ,g.JONGMOK ,g.ZIP ,g.ADDR ,g.ADDR_NUM ,g.PHONE_NB ,g.FAX ,g.WEBSITE ,g.EMAIL ,g.START_DT ,g.END_DT ,g.FOR_YN ,g.LIQ_CD FROM Strade s JOIN Gtrade g ON s.TR_CD = g.TR_CD AND s.CO_CD = g.CO_CD
AND s.TR_CD = #{TR_CD, jdbcType=VARCHAR}
AND s.TR_NM = #{TR_NM}, jdbcType=VARCHAR
AND g.REG_NB = #{REG_NB, jdbcType=VARCHAR}
AND g.PPL_NB = #{PPL_NB, jdbcType=VARCHAR}
AND s.USE_YN = #{USE_YN, jdbcType=VARCHAR}

SELECT s.CO_CD ,s.TR_CD ,s.TR_NM ,s.USE_YN ,f.FSTART_DT ,f.FEND_DT ,f.BA_NB_TR ,f.DEPOSITOR ,f.DEPOSIT_NM ,f.ACCOUNT_OPEN_BN ,f.BANK_CD ,fn.BANK_NAME FROM Strade s JOIN Ftrade f ON s.TR_CD = f.TR_CD AND s.CO_CD = f.CO_CD LEFT OUTER JOIN FINANCECODE fn ON f.BANK_CD = fn.FINANCE_CD
AND s.TR_CD = #{TR_CD, jdbcType=VARCHAR}
AND s.TR_NM = #{TR_NM, jdbcType=VARCHAR}
AND f.BA_NB_TR = #{BA_NB_TR, jdbcType=VARCHAR}
AND s.USE_YN = #{USE_YN, jdbcType=VARCHAR}

