mapper.select:
SELECT e.EMP_CD ,e.KOR_NM ,d.DEPT_NM ,w.DIV_NM FROM Employee e LEFT OUTER JOIN Department d ON e.DEPT_CD = d.DEPT_CD AND e.CO_CD = d.CO_CD LEFT OUTER JOIN Workplace w ON e.DIV_CD = w.DIV_CD AND e.CO_CD = w.CO_CD WHERE e.EMP_CD NOT IN () AND e.EMP_CD IS NOT NULL
AND e.USER_YN IN (0,1) AND e.ENRL_FG = #{ENRL_FG}
AND e.USER_YN='1' AND e.ENRL_FG = #{ENRL_FG}
AND (e.EMP_CD LIKE CONCAT('%', #{VALUE, jdbcType=VARCHAR}, '%') OR e.KOR_NM LIKE CONCAT('%', #{VALUE, jdbcType=VARCHAR}, '%') OR d.DEPT_NM LIKE CONCAT('%', #{VALUE, jdbcType=VARCHAR}, '%') OR w.DIV_NM LIKE CONCAT('%', #{VALUE, jdbcType=VARCHAR}, '%'))

SELECT s.TR_CD ,s.TR_NM ,s.TR_FG ,g.REG_NB ,g.CEO_NM ,f.BA_NB_TR ,b.BANK_NAME FROM Strade s LEFT OUTER JOIN Gtrade g ON s.TR_CD = g.TR_CD AND s.CO_CD = g.CO_CD LEFT OUTER JOIN Ftrade f ON s.TR_CD = f.TR_CD AND s.CO_CD = f.CO_CD LEFT OUTER JOIN FINANCECODE b ON f.BANK_CD = b.FINANCE_CD
s.CO_CD = #{CO_CD, jdbcType=VARCHAR}
AND s.USE_YN IN (0, 1)
AND s.USE_YN = 1
AND (s.TR_CD LIKE CONCAT('%', #{VALUE, jdbcType=VARCHAR}, '%') OR s.TR_NM LIKE CONCAT('%', #{VALUE, jdbcType=VARCHAR}, '%') OR f.BA_NB_TR LIKE CONCAT('%', #{VALUE, jdbcType=VARCHAR}, '%') OR g.REG_NB LIKE CONCAT('%', #{VALUE, jdbcType=VARCHAR}, '%') OR g.CEO_NM LIKE CONCAT('%', #{VALUE, jdbcType=VARCHAR}, '%'))
AND s.TR_FG = #{TR_FG}

